mapper.select:
SELECT dt.id , dt.username , dt.title , dt.description , dt.priority , dt.quota , dt.delete_flg deleteFlg , dt.create_date createDate , dth.done_date doneDate , dth.done_time doneTime , CASE WHEN 0 >= dt.quota - dth.done_time THEN 1 ELSE 0 END doneFlg FROM daily_task dt LEFT JOIN (SELECT * FROM daily_task_history WHERE done_date = #{doneDate}) dth ON dt.id = dth.daily_task_id WHERE dt.username = #{username}
AND dt.delete_flg = '0'
ORDER BY deleteFlg ASC , doneFlg ASC , dt.priority ASC , dt.id DESC

mapper.insert:
INSERT INTO daily_task (
    username
    , title
    , description
    , priority
    , quota
    , delete_flg
    , create_date
)
VALUES (
    #{username}
    , #{title}
    , #{description}
    , #{priority}
    , #{quota}
    , #{delete_flg}
    , #{create_date}
)
