mapper.select:
SELECT
    l.*
    , lc.name categoryName
FROM
    learning l
    LEFT JOIN learning_category lc ON l.category_Id = lc.id
WHERE
l.username = #{username}

    AND content LIKE CONCAT('%', #{search}, '%')


    AND lc.id = #{categoryId}


    AND l.id < #{nextKey}

ORDER BY id DESC
LIMIT #{limit}
