mapper.select:
SELECT id , task_id , username , comment , create_date FROM task_comment WHERE task_id = #{taskId}
AND id < #{nextKey}
ORDER BY id DESC LIMIT #{limit}

mapper.insert:
INSERT INTO task_comment (
    task_id
    , username
    , comment
    , create_date
)
VALUES (
    #{task_id}
    , #{username}
    , #{comment}
    , #{create_date}
)

mapper.update:
UPDATE task_comment
SET
    comment = #{comment}
WHERE
    id = #{id}
