mapper.select:
SELECT id , username , task , priority , status , description FROM task WHERE username = #{username}
AND status = #{status}
ORDER BY priority DESC , id DESC

mapper.insert:
INSERT INTO task (
    username
    , task
    , priority
    , status
    , description
    , plan_date
    , done_date
)
VALUES (
    #{username}
    , #{task}
    , #{priority}
    , #{status}
    , #{description}
    , #{planDate}
    , #{doneDate}
)

mapper.update:
UPDATE task
SET
    username = #{username}
    , task = #{task}
    , priority = #{priority}
    , status = #{status}
    , description = #{description}
    , plan_date = #{planDate}
    , done_date = #{doneDate}
WHERE
    id = #{id}
